import React, {Component} from "react";
// import img from '../component/img/2729411.jpg'
import styleP from '../component/css/post.module.css'
import axios from "axios";
import ReactHtmlParser from "react-html-parser";
import Menu from "../header/menu";
import { withRouter  } from 'next/router'
import Head from "next/head";



class Post extends Component {

    state={
        post:null,
        pox : [
            {id:"546"}
        ]
    };

    componentDidMount() {
        const { router } = this.props
        axios.get(`api/post/choosepost?id=${router.query.pts}`)
            .then(res=>{
                this.setState({post:res.data})
            })

    }



componentDidUpdate(prevProps, prevState, snapshot) {
    const { router } = this.props
    axios.get(`api/post/choosepost?id=${router.query.pts}`)
        .then(res=>{
            this.setState({post:res.data})
        })

    }



    render() {

        let post = (
            <div className="d-flex justify-content-center align-items-center position-absolute w-100 h-100">
                <div className="text-center">
                <i className="fas fa-3x fa-spinner fa-pulse"></i>
                <h1 className="mt-5">در حال بارگذاری ... </h1>
                </div>
            </div>


        )
        if(this.state.post != null){
        post =  (
            <section>
                <Head>
                    <title>{this.state.post.title + "طراحی سایت |"}</title>
                    <meta name="description" content={this.state.post.description} />
                    <meta name="keywords" content={this.state.post.title}/>
                    <meta name="author" content="majid javadi"/>
                </Head>
                <Menu/>
                <div className="row mr-0 ml-0">
                    <div className="col-12 pr-0 pl-0">
                        <h1 className={styleP.titleH1}>{this.state.post.title}</h1>
                        <img className={styleP.imgtop+" w-100"} src="/img/2729411.jpg"/>
                        <div className={styleP.POST}>
                            <div className={styleP.textdiv+" bg-white"}>
                           <p>

                               {ReactHtmlParser(this.state.post.context)}

                           </p>
                                <hr/>
                                {/*<div className="d-flex mb-5">*/}
                                {/*    <div className="h-100 d-flex justify-content-center align-items-center">*/}
                                {/*        <img className={styleP.imgcomment} src={woman}/>*/}
                                {/*    </div>*/}
                                {/*    <div className="mr-5">*/}
                                {/*        <h3>click</h3>*/}
                                {/*        <p>*/}
                                {/*            clickerccccccccccc*/}
                                {/*        </p>*/}
                                {/*    </div>*/}
                                {/*</div>*/}
                                {/*<div className="d-flex mt-5 justify-content-center">*/}
                                {/*    <div className="w-50">*/}
                                {/*        <div className="mb-3">*/}
                                {/*            <label htmlFor="exampleFormControlInput1" className="form-label">نام شما :</label>*/}
                                {/*            <input type="email" className="shadow form-control" id="exampleFormControlInput1"*/}
                                {/*                   placeholder="name@example.com"/>*/}
                                {/*        </div>*/}
                                {/*        <div className="mb-3">*/}
                                {/*            <label htmlFor="exampleFormControlTextarea1" className="form-label">پیام شما :</label>*/}
                                {/*            <textarea className="shadow-lg form-control" id="exampleFormControlTextarea1"*/}
                                {/*                      rows="3">*/}

                                {/*            </textarea>*/}
                                {/*        </div>*/}
                                {/*        <button className="btn btn-success w-100">*/}
                                {/*            click*/}
                                {/*        </button>*/}
                                {/*    </div>*/}
                                {/*</div>*/}
                            </div>

                        </div>
                    </div>
                </div>

            </section>
        )
        }

        return (post)
    }
}
export async function getSortedPostsData() {

    const { router } = this.props
    axios.get(`api/post/choosepost?id=${router.query.pts}`)
        .then(res=>{
            this.setState({post:res.data})
        })
    const res = await fetch(`https://api.superiorweb.ir/api/post/choosepost?id=${router.query.pts}`,{
        method:"GET",
        headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
            'Authorization':"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJzdWIiOiJtYWppZCIsImp0aSI6Ik1hamlkMDk5ME0iLCJ1bmlxdWVfbmFtZSI6Im1hamlkIiwiZXhwIjoxNjM0ODkyMjc3LCJpc3MiOiJKd3RFeGFtcGxlIiwiYXVkIjoiamF2YWRpLmNvbSJ9.gugJJgtODLU4FiuTlNErfrEEx7ZY3z8Lp2qtJOqPtdE"

        },
    })
    return res.json()
}

export default withRouter(Post);
