import styleC from '../css/Catgorys.module.css'
import img from '../img/index/project6.jpg'
import Sectiontwo from "./Sectiontwo";
import man from "../img/index/man.jpg";
import Menu from "../../header/menu";
import {Helmet} from "react-helmet";

class Language extends Component {

    render() {
        return (

            <div>
                {/*<Route path="/" />*/}
                <Menu/>
                <section
                    className={styleC.bgsection + " row justify-content-center align-items-center text-white mr-0 ml-0"}>

