                                {/*        <p>*/}
                                {/*            clickerccccccccccc*/}
                                {/*        </p>*/}
                                {/*    </div>*/}
                                {/*</div>*/}
                                {/*<div className="d-flex mt-5 justify-content-center">*/}
                                {/*    <div className="w-50">*/}
                                {/*        <div className="mb-3">*/}
                                {/*            <label htmlFor="exampleFormControlInput1" className="form-label">نام شما :</label>*/}
                                {/*            <input type="email" className="shadow form-control" id="exampleFormControlInput1"*/}
                                {/*                   placeholder="name@example.com"/>*/}
                                {/*        </div>*/}
                                {/*        <div className="mb-3">*/}
                                {/*            <label htmlFor="exampleFormControlTextarea1" className="form-label">پیام شما :</label>*/}
                                {/*            <textarea className="shadow-lg form-control" id="exampleFormControlTextarea1"*/}
                                {/*                      rows="3">*/}

                                {/*            </textarea>*/}
                                {/*        </div>*/}
                                {/*        <button className="btn btn-success w-100">*/}
                                {/*            click*/}
                                {/*        </button>*/}
                                {/*    </div>*/}
                                {/*</div>*/}
                            </div>

                        </div>
                    </div>
                </div>

            </section>
        )
        }

        return (post)
    }
}
export default Post;
