    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts').then(
            reposnse=>{
                if (reposnse.data.length === 0){
                    // console.log(reposnse.data)
                }else {
                    this.setState({result: reposnse.data.slice(0,6)})
